import styled from "styled-components";
export const FooterStyle = styled.div`
  .footer {
    background: #000;
    text-align: center;
  }

  .footer .share {
    display: flex;
    gap: 1.5rem;
    justify-content: center;
    flex-wrap: wrap;
  }

  .footer .credit {
    padding: 2.5rem 1rem;
    color: #fff;
    font-weight: normal;
    font-size: 2rem;
  }

  .footer .credit span {
    color: #ff3838;
  }
`;
