import React from "react";
import { FeaturedInfo } from "../../Styles/StylePages/Admin/FeaturedInfo";
import { MdArrowDownward, AiOutlineArrowUp } from "../../Imports/Icons";
import { useDispatch, useSelector } from "react-redux";
import CountUp from "react-countup";
const FeaturedInfos = () => {
  const { total, cancel, moth, received, compareTotal, compareTotalCancel } =
    useSelector((state) => state.products);
  return (
    <>
      <FeaturedInfo>
        <div className="featured">
          <div className="featuredItem">
            <span className="featuredTitle">Total Buy</span>
            <div className="featuredMoneyContainer">
              <span className="featuredMoney">
                $&nbsp;
                <CountUp
                  className="count"
                  start={0}
                  end={total[0].tong_doanhthu}
                  duration={5.75}
                  separator=","
                />
              </span>
            </div>
            <span className="featuredSub">Compared to last month</span>
          </div>
          <div className="featuredItem">
            <span className="featuredTitle">Cancel Bill</span>
            <div className="featuredMoneyContainer">
              <span className="featuredMoney">
                $&nbsp;
                <CountUp
                  className="count"
                  start={0}
                  end={cancel[0].tong_hd_bi_huy}
                  duration={5.75}
                  separator=","
                />
              </span>
            </div>
            <span className="featuredSub">Compared to last month</span>
          </div>
          <div className="featuredItem">
            <span className="featuredTitle">Not Received</span>
            <div className="featuredMoneyContainer">
              <span className="featuredMoney">
                $&nbsp;
                <CountUp
                  className="count"
                  start={0}
                  end={received[0].tong_hd_khach_chua_nhan_hang}
                  duration={5.75}
                  separator=","
                />
              </span>
            </div>
            <span className="featuredSub">Compared to last month</span>
          </div>
        </div>
        {/* row tow */}
        <div className="featured">
          <div className="featuredItem">
            <span className="featuredTitle">
              Total this month vs last month
            </span>
            <div className="featuredMoneyContainer">
              <span className="featuredMoney">
                $&nbsp;
                <CountUp
                  className="count"
                  start={0}
                  end={compareTotal[1].doanh_thu_thang_nay}
                  duration={5.75}
                  separator=","
                />
                <br />
                $&nbsp;
                <CountUp
                  className="count"
                  start={0}
                  end={compareTotal[1].doanh_thu_thang_truoc}
                  duration={5.75}
                  separator=","
                />
              </span>
              <span className="featuredMoneyRate">
                {compareTotal[1].sosanh_thang_truoc ===
                  "last month does not exist data" ||
                compareTotal[1].sosanh_thang_truoc < 0 ? (
                  <>
                    +
                    {compareTotal[1].sosanh_thang_truoc ===
                      "last month does not exist data" && 0}
                    <AiOutlineArrowUp className="featuredIcon" />
                  </>
                ) : (
                  <>
                    -{compareTotal[1].sosanh_thang_truoc}
                    <MdArrowDownward className="featuredIcon negative" />
                  </>
                )}
              </span>
            </div>
            <span className="featuredSub">Compared to last month</span>
          </div>
          <div className="featuredItem">
            <span className="featuredTitle">
              Cancel bill This month vs last month
            </span>
            <div className="featuredMoneyContainer">
              <span className="featuredMoney">
                $&nbsp;
                <CountUp
                  className="count"
                  start={0}
                  end={compareTotalCancel[1].tong_hd_thang_nay}
                  duration={5.75}
                  separator=","
                />
                <br />
                $&nbsp;
                <CountUp
                  className="count"
                  start={0}
                  end={compareTotalCancel[1].tong_hd_thang_truoc}
                  duration={5.75}
                  separator=","
                />
              </span>
              <span className="featuredMoneyRate">
                {compareTotalCancel[1].sosanh_thang_truoc < 0 ? (
                  <>
                    -{compareTotalCancel[1].sosanh_thang_truoc}
                    <MdArrowDownward className="featuredIcon negative" />
                  </>
                ) : (
                  <>
                    +{compareTotalCancel[1].sosanh_thang_truoc}
                    <AiOutlineArrowUp className="featuredIcon" />
                  </>
                )}
              </span>
            </div>
            <span className="featuredSub">Compared to last month</span>
          </div>
          <div className="featuredItem">
            <span className="featuredTitle">Not Received</span>
            <div className="featuredMoneyContainer">
              <span className="featuredMoney">
                $&nbsp;
                <CountUp
                  className="count"
                  start={0}
                  end={received[0].tong_hd_khach_chua_nhan_hang}
                  duration={5.75}
                  separator=","
                />
              </span>
              <span className="featuredMoneyRate">
                +2.4 <MdArrowDownward className="featuredIcon" />
              </span>
            </div>
            <span className="featuredSub">Compared to last month</span>
          </div>
        </div>
      </FeaturedInfo>
    </>
  );
};

export default FeaturedInfos;
