import React, { useState, useContext, useEffect } from "react";
import { GlobalState } from "../../Contexts/GlobalState";
import { NewProductStyle } from "../../Styles/StylePages/Admin/NewProduct";
import { useNavigate, useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import { LoadingImage } from "../../Imports/Index";
import axios from "axios";
import swal from "sweetalert";
import { toast } from "react-toastify";
const initialState = {
  ten_phieu: "",
  giatri: "",
  mota: "",
};
const NewVoucher = () => {
  const [images, setImages] = useState(false);
  const state = useContext(GlobalState);
  const { voucher } = useSelector((state) => ({ ...state.products }));
  const { token } = useSelector((state) => state.authAdmin);
  const [onEdit, setOnEdit] = useState(false);
  const [products, setProducts] = useState(initialState);
  const [loading, setLoading] = useState(false);
  const [loadingForm, setLoadingForm] = useState(false);
  const [callback, setCallback] = state.callback;
  const navigate = useNavigate();
  const { id } = useParams();
  useEffect(() => {
    if (id) {
      setOnEdit(true);
      voucher.forEach((product) => {
        if (product.id == id) {
          setProducts(product);
        }
      });
    } else {
      setOnEdit(false);
      setProducts(initialState);
      setImages(false);
    }
  }, [id, voucher]);

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setProducts({ ...products, [name]: value });
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!images)
      return swal("No Image Upload ðŸ˜….", {
        icon: "error",
      });
    try {
      if (onEdit) {
        await axios.patch(
          `/voucher/update/${products.id}`,
          { ...products },
          {
            headers: {
              Authorization: `Bearer ${token.accessToken}`,
            },
          }
        );
        swal("Edit product successfully ðŸ¤©", {
          icon: "success",
        });
      } else {
        await axios.post(
          "/payment/add",
          { ...products, public_id: images.public_id, url: images.url },
          {
            headers: {
              Authorization: `Bearer ${token.accessToken}`,
            },
          }
        );
        swal("Add product successfully ðŸ¤©", {
          icon: "success",
        });
      }

      setCallback(!callback);
      navigate("/payment");
    } catch (error) {
      alert(error.response.data.msg);
    }
  };

  return (
    <>
      <NewProductStyle />
      <div className="newProduct">
        <div className="create_product">
          <form onSubmit={handleSubmit}>
            <div className="row">
              <label htmlFor="product_id">TÃªn Phiáº¿u</label>
              <input
                type="text"
                name="ten_phieu"
                id="ten_phieu"
                required
                value={products.ten_phieu}
                onChange={handleChangeInput}
              />
              <label htmlFor="product_id">Gia Trá»‹</label>
              <input
                type="text"
                name="giatri"
                id="giatri"
                required
                value={products.giatri}
                onChange={handleChangeInput}
              />
              <label htmlFor="product_id">MÃ´ táº£</label>
              <input
                type="text"
                name="mota"
                id="mota"
                required
                value={products.mota}
                onChange={handleChangeInput}
              />
            </div>

            <button type="submit">{onEdit ? "Update" : "Create"}</button>
          </form>
        </div>
      </div>
    </>
  );
};

export default NewVoucher;
