import React, { useState, useContext, useEffect } from "react";
import { ProductIdStyle } from "../../Styles/StylePages/Admin/ProductIdStyle";
import { GlobalState } from "../../Contexts/GlobalState";
import { NewProductStyle } from "../../Styles/StylePages/Admin/NewProduct";
import { useNavigate, useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import { LoadingImage } from "../../Imports/Index";
import axios from "axios";
import swal from "sweetalert";
import { toast } from "react-toastify";
const Category = () => {
  const { categories } = useSelector((state) => state.products);
  const { token } = useSelector((state) => state.authAdmin);
  const state = useContext(GlobalState);
  const [callback, setCallback] = state.callback;
  const [category, setCategory] = useState("");
  const [onEdit, setOnEdit] = useState(false);
  const [id, setID] = useState("");
  const navigate = useNavigate();
  const createCategory = async (e) => {
    e.preventDefault();
    try {
      if (onEdit) {
        const res = await axios.patch(
          `/category/${id}`,
          { tendm: category },
          {
            headers: {
              Authorization: `Bearer ${token.accessToken}`,
            },
          }
        );
        setOnEdit(false);
        setCategory("");
        swal(`${res.data.msg} ðŸ¤©`, {
          icon: "success",
        });
      } else {
        const res = await axios.post(
          "/category",
          { tendm: category },
          {
            headers: {
              Authorization: `Bearer ${token.accessToken}`,
            },
          }
        );
        swal(`${res.data.msg} ðŸ¤©`, {
          icon: "success",
        });
        setCategory("");
      }
      setCallback(!callback);
      navigate("/category");
    } catch (error) {
      alert(error.response.data.msg);
    }
  };
  const deleteCategory = async (id) => {
    // try {
    //   let result = await swal({
    //     title: "Please login to purchase ?",
    //     icon: "warning",
    //     buttons: true,
    //     dangerMode: true,
    //   }).then((willDelete) => {
    //     if (willDelete) {
    //       let res = axios.delete(`/category/delete/${id}`, {
    //         headers: {
    //           Authorization: `Bearer ${token.accessToken}`,
    //         },
    //       });
    //       swal("navigation login Success, wait Loading... !", {
    //         icon: "success",
    //       });
    //     } else {
    //       swal("I don't have buy product ðŸ˜Ÿ'!");
    //     }
    //   });
    try {
      let result = await swal({
        title: "Are you sure?",
        text: "You won't be able to revert this!",
        type: "warning",
        showCancelButton: true,
        confirmButtonText: "Yes, delete it!",
        cancelButtonText: "No, cancel!",
        reverseButtons: true,
      });
      await axios.delete(`/category/delete/${id}`, {
        headers: {
          Authorization: `Bearer ${token.accessToken}`,
        },
      });
      setCallback(!callback);
      return result;
    } catch (e) {
      // Fail!
      console.error(e);
    }

    // if (window.confirm("Are you sure you want to delete")) {
    //   const res = await axios.delete(`/category/delete/${id}`, {
    //     headers: {
    //       Authorization: `Bearer ${token.accessToken}`,
    //     },
    //   });
    //   swal(`${res.data.msg} ðŸ˜‚`, {
    //     icon: "success",
    //   });
    //   setCallback(!callback);
    // }
    // } catch (err) {
    //   swal("Oh no lucky Thank you for ðŸ¥´!");
    // }
  };
  const editCategory = async (id, name) => {
    setID(id);
    setCategory(name);
    setOnEdit(true);
  };
  return (
    <>
      <ProductIdStyle />
      <div className="categories">
        <form onSubmit={createCategory}>
          <label htmlFor="category">Category</label>
          <input
            type="text"
            name="tendm"
            value={category}
            required
            onChange={(e) => setCategory(e.target.value)}
          />

          <button type="submit">{onEdit ? "Update" : "Create"}</button>
        </form>

        <div className="col">
          {categories.map((category) => (
            <div className="row" key={category.id}>
              <p>{category.tendm}</p>
              <div>
                <button
                  onClick={() => editCategory(category.id, category.tendm)}
                >
                  Edit
                </button>
                <button onClick={() => deleteCategory(category.id)}>
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default Category;
