import React, { useState, useRef, useEffect, useContext } from "react";
import Lottie from "react-lottie";
import { ForgetStyle } from "../../Styles/StylePages/ForgetStyle";
import { Link } from "react-router-dom";
import { defaultOptions3 } from "../../Imports/Lottie";
import { useForm } from "react-hook-form";
import { useDispatch, useSelector } from "react-redux";
import { ForgetAdminInitiate } from "../../redux/Action/ActionAdmin";
import swal from "sweetalert";
const Forget = () => {
  const { forget } = useSelector((state) => state.authAdmin);
  const {
    register,
    formState: { errors },
    handleSubmit,
    reset,
  } = useForm();
  const dispatch = useDispatch();
  const handleSubmitForm = async (data) => {
    const { email } = data;
    dispatch(ForgetAdminInitiate(email));
  };
  useEffect(() => {
    if (forget.status === 200) {
      reset();
      swal(`${forget.message}`, {
        icon: "success",
      });
    } else if (forget.status === 400) {
      swal(`${forget.message}`, {
        icon: "error",
      });
    }
  }, [forget]);
  return (
    <>
      <Metadata title="Forget-Admin" />
      <ForgetStyle>
        <div className="container1">
          <div className="forms-container">
            <div className="signin-signup">
              <form
                className="sign-in-form"
                onSubmit={handleSubmit(handleSubmitForm)}
              >
                <h2 className="title">Reset Password</h2>
                <div className="input-field">
                  <i className="fas fa-user" />
                  <input
                    {...register("email", {
                      required: true,
                      pattern: /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i,
                    })}
                    type="email"
                    placeholder="Email Address"
                    name="email"
                    id="email"
                  />
                </div>
                <span style={{ color: "red" }}>
                  {errors.email?.type === "required" &&
                    "Mời bạn nhập Email đầy đủ! "}
                  {errors?.email?.type === "pattern" &&
                    "Email của ban không hợp lệ!"}
                </span>
                <input
                  type="submit"
                  name="resetPassword"
                  className="btn solid"
                />
                <p
                  style={{
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    marginTop: "20px",
                  }}
                >
                  <Link to="/login">
                    <a href="" style={{ color: "#4590ef" }}>
                      Back Login ?
                    </a>
                  </Link>
                </p>
              </form>
            </div>
          </div>
          <div className="panels-container">
            <div className="panel left-panel">
              <div className="content">
                <h3>Forgot Password ?</h3>
                <p>
                  You forgot the password? do not worry, I will help you now.
                </p>
              </div>
              <div className="image">
                <Lottie options={defaultOptions3} />
              </div>
            </div>
          </div>
        </div>
      </ForgetStyle>
    </>
  );
};

export default Forget;
